                stream.Position = 0;
                var file = new CustomFile();
                file.ContentType = "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet";
                file.FileName = $"{DateTime.Now}.xlsx";
                file.FileContentsStream = stream;
                return file;
            }
        }
        public CustomFile ExportFileWord(int id)
        {
            var issue = _context.Issues.Find(id);

            // Save the document to a MemoryStream
            var stream = new MemoryStream();
            //Bước 1: Nạp file mẫu
            var baoCao = new Aspose.Words.Document("Template\\TemplateWord.docx");
            string nameProject = _context.Projects.Where(x => x.ProjectId == issue.ProjectId).Select(x => x.ProjectName).FirstOrDefault();
            string nameIssueType = _context.IssueTypes.Where(x => x.IssueTypeId == issue.IssueTypeId).Select(x => x.IssueTypeName).FirstOrDefault();
            string nameStatusIssue = _context.StatusIssues.Where(x => x.StatusIssueId == issue.StatusIssueId).Select(x => x.StatusIssueName).FirstOrDefault();
            string namePriority = _context.Priorities.Where(x => x.PriorityId == issue.PriorityId).Select(x => x.PriorityName).FirstOrDefault();
            string nameAssignee = _context.Users.Where(x => x.UserId == issue.AssigneeId).Select(x => x.FullName).FirstOrDefault();
            string nameReporter = _context.Users.Where(x => x.UserId == issue.ReporterId).Select(x => x.FullName).FirstOrDefault();
            string nameQC = _context.Users.Where(x => x.UserId == issue.QcactivityId).Select(x => x.FullName).FirstOrDefault();
            string nameComponent = _context.Components.Where(x => x.ComponentId == issue.ComponentId).Select(x => x.ComponentName).FirstOrDefault();
            string nameProduct = _context.Products.Where(x => x.ProductId == issue.ProductId).Select(x => x.ProductName).FirstOrDefault();
            string nameDefectOrigin = _context.DefectOrigins.Where(x => x.DefectOriginId == issue.DefectOriginId).Select(x => x.DefectOriginName).FirstOrDefault();
            string nameDefectType = _context.DefectTypes.Where(x => x.DefectTypeId == issue.DefectTypeId).Select(x => x.DefectTypeName).FirstOrDefault();
            string nameCauseCategory = _context.CauseCategories.Where(x => x.CauseCategoryId == issue.CauseCategoryId).Select(x => x.CauseCategoryName).FirstOrDefault();
            //Bước 2: Điền các thông tin cố định
            baoCao.MailMerge.Execute(new[] { "Key" }, new[] { issue.IssueId.ToString() });
            baoCao.MailMerge.Execute(new[] { "Name" }, new[] { issue.Summary });
            baoCao.MailMerge.Execute(new[] { "CreateAt" }, new[] { issue.CreateTime.ToString("dd/MM/yyyy") });
            baoCao.MailMerge.Execute(new[] { "UpdateAt" }, new[] { DateTime.Now.ToString("dd/MM/yyyy") });
            baoCao.MailMerge.Execute(new[] { "Status" }, new[] { nameStatusIssue });
            baoCao.MailMerge.Execute(new[] { "Project" }, new[] { nameProject });
            baoCao.MailMerge.Execute(new[] { "Component" }, new[] { nameComponent });
            baoCao.MailMerge.Execute(new[] { "Security" }, new[] { issue.SecurityLevel });
            baoCao.MailMerge.Execute(new[] { "Type" }, new[] { nameIssueType });
            baoCao.MailMerge.Execute(new[] { "Priority" }, new[] { namePriority });
            baoCao.MailMerge.Execute(new[] { "Reporter" }, new[] { nameReporter });
            baoCao.MailMerge.Execute(new[] { "Assignee" }, new[] { nameAssignee });
            baoCao.MailMerge.Execute(new[] { "Labels" }, new[] { issue.Labels });
            baoCao.MailMerge.Execute(new[] { "Product" }, new[] { nameProduct });
            baoCao.MailMerge.Execute(new[] { "DefectOrigin" }, new[] { nameDefectOrigin });
            baoCao.MailMerge.Execute(new[] { "QC" }, new[] { nameQC });
            baoCao.MailMerge.Execute(new[] { "CauseAnalysis" }, new[] { issue.CauseAnalysis });
            baoCao.MailMerge.Execute(new[] { "CorrectiveAction" }, new[] { issue.CorrectAction });
            baoCao.MailMerge.Execute(new[] { "DefectType" }, new[] { nameDefectType });
            baoCao.MailMerge.Execute(new[] { "CauseCategory" }, new[] { nameCauseCategory });
            baoCao.MailMerge.Execute(new[] { "Description" }, new[] { issue.Description });
            stream.Position = 0;
            baoCao.Save("Template\\Result.docx");
            byte[] fileBytes = System.IO.File.ReadAllBytes("Template\\Result.docx");
            var file = new CustomFile();
            file.ContentType = "application/vnd.openxmlformats-officedocument.wordprocessingml.document";
            file.FileName = $"{DateTime.Now}.docx";
            file.FileContents = fileBytes;
            return file;
        }
    }
    public class CustomFile
    {
        public byte[] FileContents { get; set; }
        public Stream FileContentsStream { get; set; }
        public string ContentType { get; set; }
        public string FileName { get; set; }
    }
